mapper.select:
select d.id, orderId, productId, name productName, price productPrice, quantity, cost
from easybuy_order_detail d,
     easybuy_product p
where d.isDelete = 0
  and d.productId = p.id
  and orderId = #{orderId}

mapper.delete:
update easybuy_order_detail
set isDelete = 1
where isDelete = 0

    and id = #{id}


    and orderId = #{orderId}
