mapper.update:
update easybuy_order
set status = 1
where serialNumber = #{orderNumber}

update easybuy_order


        cost = #{cost},


        status = #{status}


where isDelete = 0
and id = #{id}

mapper.select:
select o.id, userId, loginName, userAddress, o.createTime, cost, serialNumber, status, name statusName from easybuy_order o, easybuy_order_type t where o.isDelete = 0 and o.status = t.id and userId = #{userId}
and serialNumber like concat('%', #{serialNumber}, '%')
limit #{from},#{pageSize}

select o.id, userId, o.loginName, userAddress, o.createTime, cost, serialNumber, status, name statusName, mobile from easybuy_order o, easybuy_order_type t, easybuy_user u where o.isDelete = 0 and o.status = t.id and o.userId = u.id
and o.id = #{id}
and serialNumber = #{serialNumber}
